import React, { useState } from "react";
import styles from "./Form.module.css";
import Card from "../UI/Card";
import Button from "../UI/Button";

const Form = () => {
  const [cardNumber, setCardNumber] = useState("");
  const [cardNumberError, setCardNumberError] = useState(true);

  const numberChangeHandler = (event) => {
    setCardNumber(event.target.value);
  };
  const addUserCard = (event) => {
    event.preventDefault();
    if (cardNumber.trim().length < 16) {
      setCardNumberError(false);
      return;
    }

    console.log("potato");
  };

  return (
    <Card className={styles.formPosition}>
      <form onSubmit={addUserCard} className={styles.input}>
        <label htmlFor="CardHolder">CARDHOLDER NAME</label>
        <input id="CardHolder" type="text"></input>
        <label htmlFor="CardNumber">CARD NUMBER</label>
        <input
          onChange={numberChangeHandler}
          value={cardNumber}
          id="CardNumber"
          type="number"
        ></input>
        <p className={`${!cardNumberError ? styles.error : styles.p}`}>
          Wrong format, be sure all numbers are filled
        </p>

        <label className={styles.float} htmlFor="ExpDate">
          EXP.DATE (MM/YY)
        </label>
        <label htmlFor="CVC">CVC</label>
        <input className={styles.MMYY} id="ExpDate" type="number"></input>
        <input
          className={`${styles.MMYY} ${styles.YY}`}
          id="CardHolder"
          type="number"
        ></input>
        <input className={styles.cvc} id="ExpDate" type="number"></input>
        <Button className={styles.button} type="submit">
          Confirm
        </Button>
      </form>
    </Card>
  );
};

export default Form;
