import React, { useState } from "react";
import styles from "./Cards.module.css";
import cardFront from "../images/bg-card-front.png";
import cardBack from "../images/bg-card-back.png";

const Cards = (props) => {
  const [creditNum, setCreditNum] = useState(props.onNumberChange);
  const change = () => {
    if (props.onNumberChange < 0) {
      setCreditNum(props.onNumberChange);
    }
    setCreditNum("0000 0000 0000 0000");
  };
  return (
    <React.Fragment>
      <div className={styles.positionFront}>
        <img src={cardFront} className={`${styles.front}`} alt="cardFront" />
        <h4 onChange={change} className={styles.cardNumber}>
          {creditNum}
        </h4>
        <spam className={styles.cardName}>THAINA CAVAZZANI</spam>
        <spam className={styles.cardMMYY}>07/01</spam>
      </div>
      <img className={styles.back} src={cardBack} alt="cardBack" />
    </React.Fragment>
  );
};

export default Cards;
